package com.brandon.fundamentals;

public class Conditionals {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x=0, y=5;
		
		if (x != 0 & y/x > 5) System.out.println("something"); 
		//logical AND statement will execute all conditions
		
		if (x != 0 && y/x > 5) System.out.println("something");
		//conditional logical AND statement will stop when a FALSE is reached
		
	}

}
